import UIKit

enum Login {
    // MARK: Use cases

    enum Login {
        struct Request {
            var userName: String?
            var password: String?
        }
        
        struct Response {
            var isError: Bool
            var message: String?
            var user: User?
        }
        
        struct ViewModelFailedLogin {
            var message: String
        }
        
        struct ViewModelSuccessfullLogin {
            var userId: Int
        }
    }

    enum ExistingInfo {
        struct Request{}
        
        struct Response {
            var userName: String?
            var password: String?
        }
        
        struct ViewModel {
            var userName: String
            var password: String
        }
    }
}
